# Import necessary libraries
import streamlit as st
import tensorflow as tf
import cv2
import numpy as np

# Load the trained model
model = tf.keras.models.load_model(FPG5(Final).sav)  # Replace 'path_to_model_directory' with the actual directory where your trained model is saved.

# Helper function to make predictions
def predict_image(image):
    resize = tf.image.resize(image, (256, 256))
    prediction = model.predict(np.expand_dims(resize / 255, 0))
    return prediction[0][0]

# Streamlit web app
def main():
    st.title("Periodontal Image Classification")

    uploaded_image = st.file_uploader("Upload an image", type=["jpg", "png"])

    if uploaded_image is not None:
        # Read the uploaded image
        img = cv2.imdecode(np.fromstring(uploaded_image.read(), np.uint8), 1)

        # Display the uploaded image
        st.image(img, caption="Uploaded Image", use_column_width=True)

        # Make predictions
        prediction = predict_image(img)

        # Display the prediction
        if prediction > 0.5:
            st.write("Periodontal disease is present.")
        else:
            st.write("Periodontal disease is absent.")

if __name__ == "__main__":
    main()

